def sum_to_1(length):  # Randomly generate a list of numbers that sums to 1
    res = [1] * length
    while sum(res) < 100:
        res[randint(0, length - 1)] += 1
    for i in range(length):
        res[i] /= 100
    return res


def part1_2_random_cases(multiple_pickup=False):
    test_cases = []
    pickup_func = optimise_multiple_pickup if multiple_pickup else optimise_single_pickup
    for length in range(3, 15):
        for _ in range(3):
            corridor = [randint(-5, 10) for _ in range(length)]
            test = (corridor, pickup_func(corridor)[0])
            test_cases.append(test)
    return test_cases


def part1_2_not_very_random_cases(multiple_pickup=False):
    f = optimise_multiple_pickup if multiple_pickup else optimise_single_pickup
    cases = []
    for l in range(1, 20):
        all_same = [l] * l
        increasing = sorted(range(-l, l))
        decreasing = increasing[::-1]
        increasing_pwr = [i ** l for i in increasing]
        decreasing_pwr = increasing_pwr[::-1]
        same_increasing_pwr = [l ** i for i in range(l)]
        same_decreasing_pwr = increasing_pwr[::-1]
        neg_increasing_pwr = [-l ** i for i in range(l)]
        neg_decreasing_pwr = neg_increasing_pwr[::-1]
        interweaving = [l ** i * (-1) ** i for i in range(l)]
        case_set = [all_same] + [increasing] + [decreasing] + [increasing_pwr] + [decreasing_pwr]
        case_set += [same_increasing_pwr] + [same_decreasing_pwr] + [neg_increasing_pwr]
        case_set += [neg_decreasing_pwr] + [interweaving]
        cases += [(case, f(case)[0]) for case in case_set]
    return ",\n".join(str(case_tuple) for case_tuple in cases)


def part3_random_cases():
    test_cases = []
    for length in range(3, 15):
        for _ in range(3):

            shades = [(i+1)/100 for i in range(length)]
            probs = sum_to_1(length)

            test = ((shades, probs), optimal_shade_selector(shades, probs))
            test_cases.append(test)
    return test_cases


class TestAssignment2(unittest.TestCase):
    def setUp(self):
        self.nothing_robot_picks_up = [  # They're all going to be 0 here
            ([-1, -1, -2, -3, 0, 0], 0),
            ([1000, 0, -3, -5, -5, -23], 0),
            ([999], 0),
            ([234, -2], 0),
            ([9, -1], 0),
            ([9], 0),
        ]
        # random.seed(0)
        # Let's just use a random list of values which our robot will never pick up
        self.nothing_robot_picks_up += [(random.choices(range(int(-1e10), 0), k=200), 0) for _ in range(1000)]

    def check_solution_single(self, expected, corridor, choices):
        self.assertEqual(len(corridor), len(choices), msg="Solution Wrong length!")
        self.assertEqual(expected, sum(corridor[i]*choices[i] for i in range(len(choices))), msg="Wrong item value!")
        self.assertTrue(set(choices).issubset({0, 1}), msg="decisions are not one hot encoded!")
        self.assertTrue(min(itertools.accumulate(map(lambda x: 1-2*x, choices)))>=0, msg="robot runs out of energy!")

    def check_solution_multiple(self, expected, corridor, choices):
        self.check_solution_single(expected, corridor, choices)
        self.assertTrue(all(e == 0 for i, e in enumerate(itertools.accumulate(map(lambda x: 1 - 2 * x, choices))) if i < len(choices)-1 and choices[i]>choices[i+1]), msg="robot stops before it runs out of energy!")

